mapper.select:
select * from( select u.*,r.id as r_id,r.name,
rownum r from users u left join user_role ur on ur.u_id=u.id left join role r on ur.r_id=r.id
u.username like '%${keyword}%' or u.address like '%${keyword}%'
and rownum <#{currentPage}*#{pagesize}+1
)u where u.r>(#{currentPage}-1)*#{pagesize}
